const Navcomponent = styled.nav`
  width: 100%;
  position: fixed;

  bottom: 0;
  z-index: 3;
  display: flex;
  justify-content: center;

  > div {
    background: rgba(32, 33, 37, 0.9);

    width: 100%;
    box-shadow: 0px 0px 5px rgba(1, 1, 1, 0.3);
    border-radius: 17px;
    height: 70px;

    display: flex;
    justify-content: center;
    align-items: center;
    > div {
      width: 90%;
      display: flex;
      justify-content: space-evenly;
      align-items: center;
    }

    b {
      position: relative;
      transition: all 300ms !important;
      &.active {
        svg path {
          fill: var(--primary);
        }
        svg {
          filter: drop-shadow(0px 0px 2px #ff0451);
        }
        &::before {
          content: "";
          height: 10px;
          width: 10px;
          background: var(--primary);
          position: absolute;
          border-radius: 50%;
          bottom: -25%;
          left: 30%;
        }
      }
    }
  }
  @media (min-width: 800px) {
    display: none;
  }
`;
